#!/usr/bin/env node

/**
 * Script to create backend.js entry point in root directory
 * This file is required for importing: @conviso/backstage-plugin-conviso/backend.js
 * 
 * Note: dist/backend/index.js and dist/backend/index.d.ts are already generated by tsc
 */

const fs = require('fs');
const path = require('path');

const pluginRoot = path.join(__dirname, '..');
const distBackendIndexJs = path.join(pluginRoot, 'dist', 'backend', 'index.js');
const backendJs = path.join(pluginRoot, 'backend.js');

// Create backend.js entry point in root (required for npm package import)
if (fs.existsSync(distBackendIndexJs)) {
  const backendJsContent = `module.exports = require('./dist/backend/index.js');
`;
  fs.writeFileSync(backendJs, backendJsContent, 'utf8');
  console.log('✅ Created backend.js entry point');
} else {
  console.error('❌ Error: dist/backend/index.js not found. Run build first.');
  process.exit(1);
}